//new game
void GameState::NewGame() {
    player1.setPlayerAIRandomAttackNumberNULL();
    player2.setPlayerAIRandomAttackNumberNULL();
    player1.setPlayerInputNULL();
    player2.setPlayerInputNULL();
    player1.setPlayerHp(5);
    player2.setPlayerHp(5);
    setGameRun(true);
    playerArrayPt[0] = &player1;
    playerArrayPt[1] = &player2;



    //setting AI Names (if single player just set player 2's ai name otherwise setting also player 1's ai name)
    if (getGameMode() == 1 || getGameMode() == 3) {
        player2.setPlayerAIName("JACK"); //setting ai player's name
        
        
    }

    if (getGameMode() == 3) {
        player1.setPlayerAIName("KYLE");
    }
    //setting AI names

    //HUman input of names
    if (getGameMode() == 1 || getGameMode() == 2) {
        std::cout << "\nPLAYER ONE: \n\n";
        player1.setPlayerName();
        commonFunctions->WaitTime(1500);
    }

    if (getGameMode() == 2) {
        setPlayerAmount(2);
        std::cout << "\nPLAYER TWO: \n\n";
        player2.setPlayerName();
        commonFunctions->WaitTime(1500);
    }

}

