//setters
//Setting the Game Mode (1 Single Player, 2 Multiplayer, 3 Com vs. Com)
void GameState::setGameMode(int gameModeSelector) {

    GameMode = gameModeSelector;
    std::cout << "\n\n\n";
    switch (gameModeSelector) {
    case 1:
        std::cout << "SINGLE PLAYER\n\n";
       
        setPlayerAmount(1);
        break;

    case 2:
        std::cout << ".VS FIGHT\n\n";
        
        setPlayerAmount(2);
        break;

    case 3:
        std::cout << "COM VS. COM\n\n";
        setPlayerAmount(2);
        break;

    }

}

//Number of human players (1 or 2)
void GameState::setPlayerAmount(int numberofplayers) {

    playerAmount = numberofplayers;
}

//sets which player has won setPlayerWin
void GameState::setPlayerWin(int playerNum) {

    playerWin = playerNum;
}

//setting the game to run or quit GameRun
void GameState::setGameRun(bool rungame) {

    GameRun = rungame;

}





//getters
//Getting the Game Mode (1 = Single player, 2 = Multiplayer, 3 = Com Vs Com)
int GameState::getGameMode() {

    return GameMode;
}

//Number of human players (1 or 2)
int GameState::getPlayerAmount() {
    
    return playerAmount;

}

//getting which Player Won
int GameState::getPlayerWin() {
    return playerWin;
}

//get game run (whether the game is running) ->basically the quit function 
bool GameState::getGameRun() {
    return GameRun;
}
